const controls = [
  {label : 'ü•óSalad  ', type: 'salad'},
  {label : 'ü•ìBacon  ', type: 'bacon'},
  {label : 'üßÄCheese ', type: 'cheese'},
  {label : 'ü•©Meat   ', type: 'meat'},
]
const buildControls = (props) => (
<div>
  <div className={classes.BuildControls}>

      <h1>Current Price: <strong>üí∞{props.totalPrice.toFixed(2)}</strong> </h1>
      {controls.map(control => {
          return <BuildControl
           key={control.label} 
           label={control.label} 
           add={() => props.IngredientAdded(control.type)}
           remove={() => props.RemoveIngredient(control.type)}
           disabled={props.disabled[control.type]}
           />
      })}

  </div>
  <div  >
        <p> ‰∏ÄÈîÆÁÇπÈ§ê
        </p>
        <a 
         onClick={
            props.small
         }
        className={classes.OrderButton}> small</a>
        <p>
      <a  href="javascript:location.reload();" className={classes.OrderButton}> Restart</a>
      &nbsp; &nbsp; 
      <button onClick={props.click}
      className={classes.OrderButton}
       disabled={!props.prsable}>ORDER NOW</button> 
       </p> 
  </div> 
  </div>
)

const mapStateToProps = state => {
  return {
    igrds: state.burgerBuilder.ingredients,
    price: state.burgerBuilder.totalPrice,
    sum: state.burgerBuilder.sumprice
  };
}

const mapDispatchToProps = dispatch =>{
  return {
    small:()=>dispatch({type:"small"}),
    meat:()=>dispatch({type:"meat"}),
    big:()=>dispatch({type:"big"}),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(buildControls);
